mapper.update:
update zj_auction_stock_relation
set exclusive_status = 2
where auction_id = #{auctionId} and stock_number = #{stockNumber}

mapper.select:
select

from zj_auction
where goods_id = #{goodsId} and cash_price = #{price}

select

from zj_auction


        and id = #{auctionId}


        auction_name = #{auctionName}


        and goods_id = #{goodsId}
