mapper.insert:
insert into zj_permis_role (role_permis_id, role_id, permis_id
  )
values (#{rolePermisId,jdbcType=BIGINT}, #{roleId,jdbcType=BIGINT}, #{permisId,jdbcType=BIGINT}
  )

mapper.update:
update zj_permis_role
set role_id = #{roleId,jdbcType=BIGINT},
  permis_id = #{permisId,jdbcType=BIGINT}
where role_permis_id = #{rolePermisId,jdbcType=BIGINT}

mapper.select:
select role_permis_id, role_id, permis_id
from zj_permis_role
where role_permis_id = #{rolePermisId,jdbcType=BIGINT}
