mapper.update:
update zj_system_cnf
set key_name = #{keyName,jdbcType=VARCHAR},
  key_value = #{keyValue,jdbcType=VARCHAR},
  memo = #{memo,jdbcType=VARCHAR}
where key_id = #{keyId,jdbcType=BIGINT}

mapper.select:
select key_id, key_name, key_value, memo
from zj_system_cnf
where key_id = #{keyId,jdbcType=BIGINT}

select key_value
from zj_system_cnf
where key_name = #{keyName}
