mapper.insert:

  SELECT LAST_INSERT_ID()

insert into zj_wallet_record (user_id, wallet_type, transaction_type,transaction_sn,
  balance_before, change_balance, balance_after,
  remark, create_time, update_time
  )
values (#{userId,jdbcType=BIGINT}, #{walletType,jdbcType=TINYINT}, #{transactionType,jdbcType=TINYINT}, #{transactionSn,jdbcType=VARCHAR},
  #{balanceBefore,jdbcType=DECIMAL}, #{changeBalance,jdbcType=DECIMAL}, #{balanceAfter,jdbcType=DECIMAL}, 
  #{remark,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select

from zj_wallet_record


    user_id = #{userId}


    wallet_type = #{walletType}


order by create_time desc
